def.body:
@endpoint = nil
@headers = HTTP::Headers.new
@cookies = HTTP::Cookies.new
@params = {} of String => Type
@form = {} of String => Type
@json = {} of String => Type
@raw = nil
@timeout = Timeout.new
@follow = Follow.new
@features = {} of String => Feature
@tls = nil
self

if other.endpoint
  @endpoint = other.endpoint
end
@headers.merge!(other.headers)
if other.cookies != @cookies
  other.cookies.each do |cookie|
    @cookies << cookie
  end
end
if (other.timeout.connect || other.timeout.read) || other.timeout.write
  @timeout = other.timeout
end
if other.follow.updated?
  @follow = other.follow
end
if other.features.empty?
else
  @features.merge!(other.features)
end
if other.params
  @params.merge!(other.params)
end
if other.form
  @form.merge!(other.form)
end
if other.json
  @json.merge!(other.json)
end
if other.raw
  @raw = other.raw
end
if other.tls
  @tls = other.tls
end
self

if klass = Halite.feature?(name)
else
  raise(UnRegisterFeatureError.new("Not available feature: #{name}"))
end
@features[name] = klass.new(**opts)
self

if connect
  @timeout.connect = connect.to_f
end
if read
  @timeout.read = read.to_f
end
if write
  @timeout.write = write.to_f
end
self

